# The dictionary you provided
import numpy as np
import matplotlib.pyplot as plt
from matplotlib.patches import Polygon
import matplotlib.colors as mcolors


# Create a figure for the 2D projection
fig, ax = plt.subplots(figsize=(10, 8))

# Track quad positions
rows = {}
max_row_count = 0

# Create a color map for elevation
min_z = min(point[2] for quad_info in quads_data.values() for point in quad_info['coordinates'])
max_z = max(point[2] for quad_info in quads_data.values() for point in quad_info['coordinates'])
norm = mcolors.Normalize(vmin=min_z, vmax=max_z)
cmap = plt.cm.viridis

# Project each quad onto 2D (longitude, latitude) space
for quad_name, quad_info in quads_data.items():
    coords_3d = quad_info['coordinates']

    # Extract just the x, y coordinates (dropping elevation)
    coords_2d = [(point[0], point[1]) for point in coords_3d]

    # Calculate average elevation for color coding
    avg_z = sum(point[2] for point in coords_3d) / 4

    # Determine which row this quad belongs to (round elevation to group)
    row_key = round(avg_z * 2) / 2  # Round to nearest 0.5
    if row_key not in rows:
        rows[row_key] = []
    rows[row_key].append(quad_name)

    # Plot the quad
    polygon = Polygon(coords_2d, closed=True,
                      alpha=0.7,
                      edgecolor='black',
                      facecolor=cmap(norm(avg_z)))
    ax.add_patch(polygon)

    # Optional: add quad labels
    centroid = quad_info['centroid']
    ax.text(centroid[0], centroid[1], quad_name.split('_')[1],
            ha='center', va='center', fontsize=8)


# Find the count of quads in each row
print("Quads per row (by elevation):")
for row in sorted(rows.keys()):
    quad_list = rows[row]
    print(f"Row at elevation ~{row:.2f}: {len(quad_list)} quads - {quad_list}")
    max_row_count = max(max_row_count, len(quad_list))

# Set axis limits based on the data
x_coords = [point[0] for quad in quads_data.values() for point in quad['coordinates']]
y_coords = [point[1] for quad in quads_data.values() for point in quad['coordinates']]
margin = 0.00001
ax.set_xlim(min(x_coords) - margin, max(x_coords) + margin)
ax.set_ylim(min(y_coords) - margin, max(y_coords) + margin)

# Labels and title
ax.set_xlabel('Longitude')
ax.set_ylabel('Latitude')
ax.set_title('2D Projection of Rooftop Quads')

# Add a properly initialized colorbar
sm = plt.cm.ScalarMappable(cmap=cmap, norm=norm)
sm.set_array([])  # This line is crucial - it initializes the ScalarMappable
cbar = plt.colorbar(sm, ax=ax)
cbar.set_label('Elevation (m)')




plt.show()
plt.axis('equal')
plt.tight_layout()
plt.savefig('rooftop_2d_projection.png', dpi=300)